import { Navigate, Route } from 'react-router-dom';
import { useAuth } from './authContext';

function ProtectedRoute({ path, element }) {
  const { isLoggedIn } = useAuth();
  return isLoggedIn ? <Route path={path} element={element} /> : <Navigate to="/login" replace />;
}

export function PrivateRoutes() {
  return (
    <>
      <ProtectedRoute path="/roles" element={<Roles />} />
      <ProtectedRoute path="/foodds" element={<Foodds />} />
      <ProtectedRoute path="/foodrequests" element={<FoodRequests />} />
      <ProtectedRoute path="/industryp" element={<Industryp />} />
    </>
  );
}
