import React, { useState, useEffect } from 'react';
import './App.css';
import AboutUs from './components/AboutUs';
import Home from './components/Home';
import Navbar from './components/Navbar';
import Services from './components/Services';
import ContactUs from './components/ContactUs';
import Footer from './components/Footer';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Roles from './components/Roles';
import Logout from './components/Logout';
import ProtectedRoute from './ProtectedRoute';
import FoodDS from './components/FoodDS';
import FoodRequests from './components/FoodRequests';
import IndustryP from './components/IndustryP';

function App() {

  // Check if the user is logged in
  const [auth, setAuth] = useState(false);

  const isLoggedIn = async () => {
    try {
      const res = await fetch('/auth', {
        method: 'GET',
        headers: {
          Accept: "applicaiton/json",
          "Content-Type": "application/json"
        },
        credentials: "include"
      });

      if (res.status === 200) {
        setAuth(true);
      }
      if (res.status === 401) {
        setAuth(false);
      }

    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    isLoggedIn();
  }, []);

  return (
    <>
      <Navbar auth={auth1} />
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route exact path="/aboutus" element={<AboutUs />} />
        <Route exact path="/service" element={<Services />} />
        <Route exact path="/contactus" element={<ContactUs />} />
        <Route exact path="/login" element={<Login />} />
        <Route exact path="/signup" element={<SignUp />} />
        <ProtectedRoute path="/roles" element={<Roles />} auth={auth}>
          <Route exact path="/logout" element={<Logout />} />
          <Route exact path="/foodds" element={<FoodDS />} />
          <Route exact path="/foodrequests" element={<FoodRequests />} />
          <Route exact path="/industryp" element={<IndustryP />} />
        </ProtectedRoute>
        {auth ? <Navigate to="/roles" /> : <Navigate to="/" />}
      </Routes>
      <Footer />
    </>
  );
}

export default App;
